import React, { useState, useEffect } from 'react';
import { supabase } from '../lib/supabase';
import { BrutalCard } from '../components/BrutalCard';
import { BrutalButton } from '../components/BrutalButton';
import { PhoneInput } from '../components/PhoneInput';
import { Plus, Search, User, Star, ChevronRight, MessageCircle } from 'lucide-react';
import { Link, useSearchParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { formatPhone } from '../utils/formatters';

export const Clients: React.FC = () => {
    const { user, userType, region } = useAuth();
    const [searchParams] = useSearchParams();
    const [clients, setClients] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState(searchParams.get('search') || '');
    const [showModal, setShowModal] = useState(false);

    // Form State
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [photo, setPhoto] = useState<File | null>(null);
    const [uploading, setUploading] = useState(false);

    const isBeauty = userType === 'beauty';
    const buttonClass = isBeauty ? 'bg-beauty-neon hover:bg-beauty-neonHover text-black' : 'bg-accent-gold hover:bg-accent-goldHover text-black';

    const fetchClients = async () => {
        try {
            // Fetch clients, filtering out inactive ones
            const { data, error } = await supabase
                .from('clients')
                .select('*')
                .eq('user_id', user.id)
                .neq('is_active', false) // Filter out inactive clients (soft deleted)
                .order('name', { ascending: true });

            if (error) throw error;

            if (data) {
                // Fetch actual visit counts from completed appointments for all clients
                const clientIds = data.map(c => c.id);

                const { data: appointmentCounts, error: countError } = await supabase
                    .from('appointments')
                    .select('client_id')
                    .in('client_id', clientIds)
                    .eq('status', 'Completed')
                    .eq('user_id', user.id);

                if (!countError && appointmentCounts) {
                    // Count visits per client
                    const visitCounts: Record<string, number> = {};
                    appointmentCounts.forEach(apt => {
                        visitCounts[apt.client_id] = (visitCounts[apt.client_id] || 0) + 1;
                    });

                    // Merge visit counts into client data
                    const clientsWithVisits = data.map(client => ({
                        ...client,
                        actual_visits: visitCounts[client.id] || 0
                    }));

                    setClients(clientsWithVisits);
                } else {
                    setClients(data);
                }
            }
        } catch (error) {
            console.error('Error fetching clients:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchClients();
    }, []);

    useEffect(() => {
        const querySearch = searchParams.get('search');
        if (querySearch) {
            setSearchTerm(querySearch);
        }
    }, [searchParams]);

    const handleCreateClient = async (e: React.FormEvent) => {
        e.preventDefault();
        setUploading(true);
        try {
            const { data: { user } } = await supabase.auth.getUser();
            if (!user) throw new Error('Usuário não autenticado');

            let photoUrl = null;

            // Upload photo if provided - make this optional and non-blocking
            if (photo) {
                try {
                    const fileExt = photo.name.split('.').pop();
                    const fileName = `${user.id}/${Date.now()}.${fileExt}`;

                    console.log('Attempting to upload photo to client_photos bucket...');

                    const { data: uploadData, error: uploadError } = await supabase.storage
                        .from('client_photos')
                        .upload(fileName, photo);

                    if (uploadError) {
                        console.error('Photo upload error:', uploadError);
                        // Don't throw - just warn the user and continue without photo
                        alert('Aviso: Não foi possível fazer upload da foto. O cliente será criado sem foto.');
                    } else {
                        const { data: { publicUrl } } = supabase.storage
                            .from('client_photos')
                            .getPublicUrl(fileName);
                        photoUrl = publicUrl;
                        console.log('Photo uploaded successfully:', publicUrl);
                    }
                } catch (photoError) {
                    console.error('Photo upload exception:', photoError);
                    alert('Aviso: Erro ao fazer upload da foto. O cliente será criado sem foto.');
                }
            }

            // Create client regardless of photo upload success
            const { error } = await supabase.from('clients').insert({
                user_id: user.id,
                name,
                email,
                phone,
                photo_url: photoUrl,
                loyalty_tier: 'Bronze',
                total_visits: 0,
                rating: 0
            });

            if (error) throw error;

            alert('Cliente criado com sucesso!');
            setShowModal(false);
            fetchClients();
            setName('');
            setEmail('');
            setPhone('');
            setPhoto(null);
        } catch (error: any) {
            console.error('Error creating client:', error);
            alert(`Erro ao criar cliente: ${error.message || JSON.stringify(error)}`);
        } finally {
            setUploading(false);
        }
    };

    const filteredClients = clients.filter(client => {
        const matchesSearch = client.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            client.phone?.includes(searchTerm);
        return matchesSearch;
    });

    return (
        <div className="space-y-6 relative">
            <div className="flex flex-col md:flex-row justify-between items-start md:items-center border-b-4 border-white/10 pb-4 gap-4">
                <h2 className="text-2xl md:text-4xl font-heading text-white uppercase">Clientes</h2>
                <BrutalButton variant="primary" icon={<Plus />} onClick={() => setShowModal(true)} className="w-full md:w-auto">Novo Cliente</BrutalButton>
            </div>



            {/* Search Bar */}
            <div className="relative">
                <Search className={`absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 ${isBeauty ? 'text-beauty-neon/50' : 'text-neutral-500'}`} />
                <input
                    type="text"
                    placeholder="Buscar por nome ou telefone..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className={`
                        w-full p-4 pl-12 text-white font-mono text-sm outline-none transition-all duration-300
                        ${isBeauty
                            ? 'bg-beauty-card/50 border border-beauty-neon/20 rounded-xl focus:border-beauty-neon focus:shadow-neon'
                            : 'bg-black/40 border-2 border-neutral-800 focus:border-accent-gold focus:shadow-heavy-sm'
                        }
                    `}
                />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {loading ? (
                    <div className="col-span-full text-center text-text-secondary p-10">Carregando clientes...</div>
                ) : filteredClients.length === 0 ? (
                    <div className="col-span-full text-center text-text-secondary p-10">Nenhum cliente encontrado.</div>
                ) : (
                    filteredClients.map(client => {
                        const rating = client.rating || 0;
                        // Use actual_visits from our query, fallback to total_visits column
                        const totalVisits = client.actual_visits ?? client.total_visits ?? 0;

                        return (
                            <Link key={client.id} to={`/clientes/${client.id}`}>
                                <BrutalCard className="hover:border-white/40 transition-colors cursor-pointer group h-full relative">
                                    <div className="flex items-start justify-between mb-4">
                                        <div className={`w-12 h-12 rounded-full border-2 ${isBeauty ? 'border-beauty-neon' : 'border-accent-gold'} flex items-center justify-center bg-black overflow-hidden`}>
                                            {client.photo_url ? (
                                                <img src={client.photo_url} alt={client.name} className="w-full h-full object-cover" />
                                            ) : (
                                                <User className={isBeauty ? 'text-beauty-neon' : 'text-accent-gold'} />
                                            )}
                                        </div>
                                        <div className="flex flex-col items-end gap-1">
                                            <span className="text-xs font-mono text-neutral-400">
                                                {totalVisits} visita{totalVisits !== 1 ? 's' : ''}
                                            </span>
                                            {rating > 0 && (
                                                <div className="flex gap-0.5">
                                                    {[1, 2, 3, 4, 5].map(i => (
                                                        <Star
                                                            key={i}
                                                            className={`w-3 h-3 ${i <= rating ? 'fill-accent-gold text-accent-gold' : 'text-neutral-600'}`}
                                                        />
                                                    ))}
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                    <div className="flex items-center justify-between">
                                        <div>
                                            <h3 className={`text-lg font-heading text-white mb-1 group-hover:${isBeauty ? 'text-beauty-neon' : 'text-accent-gold'} transition-colors`}>{client.name}</h3>
                                            <p className="text-sm text-text-secondary font-mono flex items-center gap-2">
                                                {client.phone ? formatPhone(client.phone, region as any) : 'Sem telefone'}
                                                {client.phone && (
                                                    <button
                                                        onClick={(e) => {
                                                            e.preventDefault();
                                                            e.stopPropagation();
                                                            window.open(`https://wa.me/${client.phone.replace(/\D/g, '')}`, '_blank');
                                                        }}
                                                        className={`p-1 rounded-full hover:bg-green-500/10 transition-colors ${isBeauty ? 'text-green-400 hover:text-green-300' : 'text-green-600 hover:text-green-500'}`}
                                                        title="WhatsApp"
                                                    >
                                                        <MessageCircle className="w-4 h-4" />
                                                    </button>
                                                )}
                                            </p>
                                        </div>
                                        {/* Clickable Indicator */}
                                        <ChevronRight className={`w-5 h-5 text-neutral-600 group-hover:${isBeauty ? 'text-beauty-neon' : 'text-accent-gold'} group-hover:translate-x-1 transition-all`} />
                                    </div>
                                </BrutalCard>
                            </Link>
                        );
                    })
                )}
            </div>

            {/* Modal */}
            {showModal && (
                <div className={`fixed inset-0 z-50 flex items-center justify-center p-4 ${isBeauty ? 'bg-beauty-dark/80' : 'bg-black/85'}`}>
                    <div className={`
                        w-full max-w-md p-6 relative modal-enter
                        ${isBeauty
                            ? 'bg-gradient-to-br from-beauty-card to-beauty-dark border border-beauty-neon/30 rounded-2xl shadow-[0_0_20px_rgba(167,139,250,0.15)]'
                            : 'bg-brutal-card border-4 border-brutal-border shadow-heavy-lg'
                        }
                    `}>
                        <button
                            onClick={() => setShowModal(false)}
                            className={`absolute top-4 right-4 p-1.5 rounded-full transition-all ${isBeauty ? 'text-beauty-neon/60 hover:text-beauty-neon hover:bg-beauty-neon/10' : 'text-neutral-500 hover:text-white hover:bg-neutral-800'}`}
                        >
                            <span className="text-xl font-bold">×</span>
                        </button>
                        <h3 className={`text-xl font-heading text-white mb-6 ${isBeauty ? '' : 'uppercase'}`}>Novo Cliente</h3>

                        <form onSubmit={handleCreateClient} className="space-y-4">
                            <div>
                                <label className={`block text-xs font-mono mb-2 ${isBeauty ? 'text-beauty-neon/70' : 'text-neutral-500'}`}>Nome Completo</label>
                                <input
                                    type="text"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    className={`
                                        w-full p-3 text-white outline-none transition-all duration-300
                                        ${isBeauty
                                            ? 'bg-beauty-dark/50 border border-beauty-neon/20 rounded-xl focus:border-beauty-neon focus:shadow-[0_0_10px_rgba(167,139,250,0.2)]'
                                            : 'bg-black border-2 border-neutral-700 focus:border-accent-gold'
                                        }
                                    `}
                                    required
                                />
                            </div>

                            <div>
                                <label className={`block text-xs font-mono mb-2 ${isBeauty ? 'text-beauty-neon/70' : 'text-neutral-500'}`}>Telefone</label>
                                <PhoneInput
                                    value={phone}
                                    onChange={setPhone}
                                    defaultRegion={region as 'BR' | 'PT'}
                                    className="w-full"
                                />
                            </div>

                            <div>
                                <label className={`block text-xs font-mono mb-2 ${isBeauty ? 'text-beauty-neon/70' : 'text-neutral-500'}`}>Email (Opcional)</label>
                                <input
                                    type="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    className={`
                                        w-full p-3 text-white outline-none transition-all duration-300
                                        ${isBeauty
                                            ? 'bg-beauty-dark/50 border border-beauty-neon/20 rounded-xl focus:border-beauty-neon focus:shadow-[0_0_10px_rgba(167,139,250,0.2)]'
                                            : 'bg-black border-2 border-neutral-700 focus:border-accent-gold'
                                        }
                                    `}
                                />
                            </div>

                            <div>
                                <label className={`block text-xs font-mono mb-2 ${isBeauty ? 'text-beauty-neon/70' : 'text-neutral-500'}`}>Foto (Opcional)</label>
                                <input
                                    type="file"
                                    accept="image/*"
                                    onChange={(e) => setPhoto(e.target.files?.[0] || null)}
                                    className={`
                                        w-full p-3 text-white outline-none transition-all duration-300
                                        ${isBeauty
                                            ? 'bg-beauty-dark/50 border border-beauty-neon/20 rounded-xl file:mr-4 file:py-2 file:px-4 file:border-0 file:rounded-lg file:text-sm file:font-semibold file:bg-beauty-neon file:text-black hover:file:bg-beauty-neonHover'
                                            : 'bg-black border-2 border-neutral-700 file:mr-4 file:py-2 file:px-4 file:border-0 file:text-sm file:font-semibold file:bg-accent-gold file:text-black hover:file:bg-accent-goldHover'
                                        }
                                    `}
                                />
                                <p className="text-xs text-neutral-500 mt-1">Formatos aceitos: JPG, PNG</p>
                            </div>

                            <BrutalButton
                                type="submit"
                                variant="primary"
                                fullWidth
                                loading={uploading}
                                className="mt-4"
                            >
                                {uploading ? 'Cadastrando...' : 'Cadastrar'}
                            </BrutalButton>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

import React, { useState } from 'react';
import { BrutalCard } from '../components/BrutalCard';
import { BrutalButton } from '../components/BrutalButton';
import { Clock, AlertTriangle, ArrowRight } from 'lucide-react';
import { ProfitMetrics } from '../components/dashboard/ProfitMetrics';
import { ActionCenter } from '../components/dashboard/ActionCenter';
import { useAuth } from '../contexts/AuthContext';
import { useAlerts } from '../contexts/AlertsContext';
import { useNavigate } from 'react-router-dom';
import { InfoButton, AIAssistantButton } from '../components/HelpButtons';
import { GoalHistory } from '../components/GoalHistory';

import { formatCurrency } from '../utils/formatters';
import { useAppTour } from '../hooks/useAppTour';
import { useDashboardData } from '../hooks/useDashboardData';

export const Dashboard: React.FC = () => {
  const { userType, region } = useAuth();
  const { alerts } = useAlerts();
  const navigate = useNavigate();
  const { startTour } = useAppTour();

  const {
    appointments,
    loading,
    monthlyGoal,
    goalHistory,
    updateGoal,
    profitMetrics,
    actionItems
  } = useDashboardData();

  const [isEditingGoal, setIsEditingGoal] = useState(false);
  const [newGoal, setNewGoal] = useState(monthlyGoal.toString());

  const isBeauty = userType === 'beauty';
  const currencyRegion = region === 'PT' ? 'PT' : 'BR';
  const currencySymbol = region === 'PT' ? '€' : 'R$';
  const accentText = isBeauty ? 'text-beauty-neon' : 'text-accent-gold';

  const handleSaveGoal = async () => {
    const goalValue = parseFloat(newGoal);
    if (isNaN(goalValue)) return;

    const { error } = await updateGoal(goalValue);

    if (error) {
      console.error("Error updating goal:", error);
      alert("Erro ao atualizar a meta.");
    } else {
      setIsEditingGoal(false);
    }
  };

  return (
    <div className="space-y-4 md:space-y-8">
      <div className="flex flex-col md:flex-row justify-between items-start md:items-end border-b-2 md:border-b-4 border-white/10 pb-4 md:pb-6 gap-3 md:gap-4 animate-in fade-in slide-in-from-top-4 duration-500">
        <div>
          <div className="flex items-center gap-2">
            <h2 className="text-sm md:text-xl font-heading text-text-secondary uppercase tracking-widest">Painel de Controle</h2>
            <AIAssistantButton context="o painel geral e suas métricas de lucro" />
          </div>
          <h1 className="text-2xl md:text-5xl font-heading text-white mt-1">
            Visão Geral de <span className={accentText}>Lucro</span>
          </h1>
        </div>
        <div className="flex gap-2 w-full md:w-auto">
          <button
            onClick={startTour}
            className="p-2 md:p-3 rounded-lg border border-neutral-800 hover:bg-white/5 text-neutral-400 hover:text-white transition-colors"
            title="Iniciar Tour Guiado"
          >
            <InfoButton text="Tutorial" />
          </button>
          <BrutalButton
            id="dashboard-new-appointment"
            variant="primary"
            size="md"
            icon={<Clock />}
            className="w-full md:w-auto flex-1 text-sm md:text-base h-11 md:h-auto"
            onClick={() => navigate('/agenda')}
          >
            Novo Agendamento
          </BrutalButton>
        </div>
      </div>

      <div className="animate-in fade-in slide-in-from-bottom-4 duration-500 delay-150">
        <ProfitMetrics
          metrics={profitMetrics}
          currencySymbol={currencySymbol}
          currencyRegion={currencyRegion}
          isBeauty={isBeauty}
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-4 md:gap-6 animate-in fade-in slide-in-from-bottom-4 duration-500 delay-300">
        <div className="lg:col-span-1 h-full">
          <ActionCenter
            actions={actionItems}
            onActionClick={(action) => console.log('Action clicked:', action)}
            isBeauty={isBeauty}
          />
        </div>

        <div className="lg:col-span-2 space-y-4 md:space-y-6">
          <BrutalCard
            id="dashboard-appointments-list"
            title={
              <div className="flex items-center gap-2">
                <span>Próximos Atendimentos</span>
                <InfoButton text="Seus próximos compromissos para hoje." />
              </div>
            }
            className="brutal-card-enhanced"
            noPadding
          >
            {loading ? (
              <div className="p-4 text-center text-text-secondary">Carregando agendamentos...</div>
            ) : (
              <ul className="divide-y-2 divide-neutral-800">
                {appointments.map((apt) => (
                  <li
                    key={apt.id}
                    className="p-3 md:p-4 hover:bg-white/5 transition-colors flex items-center justify-between group cursor-pointer"
                    onClick={() => navigate(`/agenda?date=${apt.rawDate}`)}
                  >
                    <div className="flex items-center gap-3 md:gap-4">
                      <div className={`font-mono text-base md:text-xl font-bold ${accentText} bg-neutral-900 px-2 py-1 md:px-3 md:py-2 border border-neutral-700 flex flex-col items-center min-w-[70px]`}>
                        <span>{apt.time}</span>
                        <span className="text-[10px] md:text-xs opacity-70 mt-1">
                          {new Date(apt.appointment_time).toLocaleDateString('pt-BR', { day: '2-digit', month: '2-digit' })}
                        </span>
                      </div>
                      <div>
                        <p className="font-heading text-sm md:text-lg text-white">{apt.clientName}</p>
                        <p className="text-[10px] md:text-sm text-text-secondary font-mono">{apt.service}</p>
                      </div>
                    </div>
                    <div className="opacity-100 lg:opacity-0 lg:group-hover:opacity-100 transition-opacity">
                      <BrutalButton size="sm" variant="ghost">Ver</BrutalButton>
                    </div>
                  </li>
                ))}
                {appointments.length === 0 && (
                  <li className="p-4 text-center text-text-secondary">Nenhum agendamento encontrado.</li>
                )}
              </ul>
            )}
            <div className="p-3 border-t-2 border-dashed border-neutral-700">
              <button
                onClick={() => navigate('/agenda')}
                className={`w-full py-1 text-center text-xs font-mono text-text-secondary hover:${accentText} uppercase tracking-widest transition-colors`}
              >
                Ver Agenda Completa →
              </button>
            </div>
          </BrutalCard>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <BrutalCard className="h-full brutal-card-enhanced gold-accent-border" noPadding>
              <div className="p-4">
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center gap-2">
                    <span className="text-xs font-bold uppercase text-text-secondary">Meta Mensal</span>
                  </div>
                  <span className={`text-sm font-mono font-bold ${accentText}`}>{formatCurrency(monthlyGoal, currencyRegion)}</span>
                </div>
                <div className="w-full bg-neutral-800 h-2 rounded-full overflow-hidden mt-2">
                  <div className={`h-full ${isBeauty ? 'bg-beauty-neon' : 'bg-accent-gold'}`} style={{ width: '65%' }}></div>
                </div>
                <p className="text-[10px] text-text-secondary mt-2 text-right">65% atingido</p>
              </div>
            </BrutalCard>

            <BrutalCard className="h-full brutal-card-enhanced" noPadding>
              <div className="p-4">
                <div className="flex items-center gap-2 mb-3">
                  <AlertTriangle className="w-4 h-4 text-yellow-500" />
                  <span className="text-xs font-bold uppercase text-white">Avisos do Sistema</span>
                </div>
                {alerts.length > 0 ? (
                  <div className="space-y-2">
                    {alerts.slice(0, 2).map(alert => (
                      <div key={alert.id} className="text-xs text-text-secondary border-l-2 border-yellow-500 pl-2">
                        {alert.text}
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-xs text-text-secondary">Tudo operando normalmente.</p>
                )}
              </div>
            </BrutalCard>
          </div>
        </div>
      </div>
    </div>
  );
};

import React from 'react';
import { BrutalCard } from '../components/BrutalCard';
import { useAuth } from '../contexts/AuthContext';
import {
    Megaphone, Sparkles, Calendar, Target,
    MessageSquare, ImageIcon, Wand2, Clock, Bell, Info
} from 'lucide-react';
import { BrutalButton } from '../components/BrutalButton';

export const Marketing: React.FC = () => {
    const { userType } = useAuth();

    const isBeauty = userType === 'beauty';
    const accentColor = isBeauty ? 'beauty-neon' : 'accent-gold';
    const accentText = isBeauty ? 'text-beauty-neon' : 'text-accent-gold';
    const accentBg = isBeauty ? 'bg-beauty-neon' : 'bg-accent-gold';

    const upcomingFeatures = [
        {
            icon: Megaphone,
            title: 'Campanhas Automatizadas',
            description: 'Dispare mensagens de WhatsApp, SMS e E-mail para seus clientes de forma segmentada.'
        },
        {
            icon: Sparkles,
            title: 'Editor de Fotos IA',
            description: 'Melhore suas fotos de cortes e serviços automaticamente com inteligência artificial.'
        },
        {
            icon: Calendar,
            title: 'Calendário de Conteúdo',
            description: 'Receba sugestões diárias do que postar nas suas redes sociais para atrair mais clientes.'
        },
        {
            icon: Target,
            title: 'Inteligência de Público',
            description: 'Identifique clientes que não voltam há tempos e crie campanhas de reativação automáticas.'
        }
    ];

    return (
        <div className="space-y-6 md:space-y-8 pb-20">
            {/* Header */}
            <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4 border-b-4 border-white/10 pb-4">
                <div>
                    <h2 className="text-2xl md:text-4xl font-heading text-white uppercase">Marketing Inteligente</h2>
